import { useEffect, useState } from 'react';
import avatar from '../icons/avatar.png';
import { User, getAuth, onAuthStateChanged, signInAnonymously, updateProfile } from "firebase/auth";
import { get, getDatabase, ref, set } from 'firebase/database';

const contactMeForOptions = [
  "Everything",
  "Events",
  "Exhibitions"
]

const languageOptions = [
  "German",
  "French",
  "English"
]

const commentFilterOptions = [
  "None",
  "Curator Only",
  "Kids Friendly"
]

const languageIcons = [
  `<svg width="35" height="37" viewBox="0 0 35 37" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
  <rect width="35" height="37" fill="url(#pattern0)"/>
  <defs>
  <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
  <use xlink:href="#image0_6_261" transform="matrix(0.00183851 0 0 0.00173913 -0.000993789 0)"/>
  </pattern>
  <image id="image0_6_261" width="545" height="575" xlink:href="data:image/png;base64,"/>
  </defs>
  </svg>`,
  `<svg width="29" height="43" viewBox="0 0 29 43" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
  <rect width="29" height="43" fill="url(#pattern0)"/>
  <defs>
  <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
  <use xlink:href="#image0_6_259" transform="matrix(0.00250627 0 0 0.00169027 0 -0.00201084)"/>
  </pattern>
  <image id="image0_6_259" width="399" height="594" xlink:href="data:image/png;base64,"/>
  </defs>
  </svg>`,
  `<svg width="41" height="33" viewBox="0 0 41 33" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
  <rect width="41" height="33" fill="url(#pattern0)"/>
  <defs>
  <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
  <use xlink:href="#image0_6_263" transform="matrix(0.00143885 0 0 0.00178766 0 -0.00501417)"/>
  </pattern>
  <image id="image0_6_263" width="695" height="565" xlink:href="data:image/png;base64,"/>
  </defs>
  </svg>`
]

function Title({ children }: { children: React.ReactNode }) {
  return <p className='underline font-bold text-[1em]'>{children}</p>;
}

function Description({ children }: { children: React.ReactNode }) {
  return <p className='font-extralight italic text-[0.8em]'>{children}</p>;
}

function Input({ value, onChange }: { value: string; onChange?: (e: React.ChangeEvent<HTMLInputElement>) => void }) {
  return <input
    style={{ background: '#EEEEEE' }}
    className='border-1 border-gray-300 h-[30px] backgro rounded-md p-2 min-w-full'
    type='text'
    value={value}
    onChange={onChange}
  />;
}

export function SettingsPage() {
  const [user, setUser] = useState<User | null>(null)
  const [displayName, setDisplayName] = useState('')
  const [profile, setProfile] = useState<{[key:string]: any} | null>(null)

  useEffect(() => {
    const auth = getAuth();
    signInAnonymously(auth)
      .then(() => {
        onAuthStateChanged(auth, setUser)
      })
      .catch((error) => {
        console.log(error)
      })
  }, [])

  useEffect(() => {
    if(user!== null) {
      setDisplayName(user.displayName ?? '...')
      get(ref(getDatabase(), `/users/${user.uid}`)).then(snapshot => {
        setProfile(snapshot.val() ?? {})
      })
    }
  }, [user])

  useEffect(() => {
    const h = setTimeout(() => {
      if(user !== null) {
        updateProfile(user, {
          displayName
        })
      }
    }, 500)

    return () => clearTimeout(h)
  }, [user, displayName])

  useEffect(() => {
    const h = setTimeout(() => {
      if(user !== null && profile !== null) {
        set(ref(getDatabase(), `/users/${user.uid}`), profile)
      }
    }, 500)

    return () => clearTimeout(h)
  }, [user, profile])

  const contactMeForIdx = Math.max(
    contactMeForOptions.findIndex(v => v === profile?.['contactMeFor']),
    0)

  const languageIdx = Math.max(
    languageOptions.findIndex(v => v === profile?.['language']),
    0)

  const commentFilterIdx = Math.max(
    commentFilterOptions.findIndex(v => v === profile?.['commentFilter']),
    0)

  return (
    <section className='p-3'>
      <img style={{ borderRadius: '5px', margin: 'auto' }} src={avatar} alt='avatar' />
      <div className='mt-8'>
        <Title>Display Name</Title>
        <Description>From now on, you will be known as ... </Description>
        <Input
          value={displayName}
          onChange={e => {
            setDisplayName(e.target.value ?? '...')
          }}
        />
      </div>
      <div className='mt-4'>
        <Title>Contact</Title>
        <Description>leave us your email address if you want to engage in fistfight with random strangers</Description>
        <Input
          value={profile?.['contact']}
          onChange={e => {
            setProfile((v: any) => ({
              ...(v ?? {}),
              contact: e.target.value
            }))
          }}
        />
      </div>
      <div className='mt-8'>
        <Description>contact me for :</Description>
        <div style={{
          position: 'relative',
          height: 28
        }}
        onClick={() => {
          setProfile(v => ({
            ...(v ?? {}),
            contactMeFor: contactMeForOptions[
              (contactMeForIdx+1)%contactMeForOptions.length
            ]
          }))
        }}>
          <div  style={{
            borderBottom: '2px solid black',
            position: 'absolute',
            top: 13,
            left: 0,
            width: '100%'
          }}/>
          <div  style={{
            borderBottom: '1px solid black',
            width: 14,
            height: 14,
            backgroundColor: '#D9D9D9',
            border: '3px solid #000',
            borderRadius: 7,
            position: 'absolute',
            top: 7,
            marginLeft: -7,
            transition: 'left 0.3s',
            left: `${2 + ((96/(contactMeForOptions.length-1)) * contactMeForIdx)}%`
          }}/>
        </div>
        <b style={{fontSize: 12}}>
          {contactMeForOptions[contactMeForIdx]}
        </b>
      </div>

      <div className='flex justify-center items-center gap-3'>
        <div
          className='w-10'
          dangerouslySetInnerHTML={{__html: languageIcons[languageIdx]}}
        />
        <div className='mt-8 flex-1'>
          <Title>Interface language</Title>
          <div style={{
            position: 'relative',
            height: 28
          }}
          onClick={() => {
            setProfile(v => ({
              ...(v ?? {}),
              language: languageOptions[
                (languageIdx+1)%languageOptions.length
              ]
            }))
          }}>
            <div  style={{
              borderBottom: '2px solid black',
              position: 'absolute',
              top: 13,
              left: 0,
              width: '100%'
            }}/>
            <div  style={{
              borderBottom: '1px solid black',
              width: 14,
              height: 14,
              backgroundColor: '#D9D9D9',
              border: '3px solid #000',
              borderRadius: 7,
              position: 'absolute',
              top: 7,
              marginLeft: -7,
              transition: 'left 0.3s',
              left: `${2 + ((96/(languageOptions.length-1)) * languageIdx)}%`
            }}/>
          </div>
          <b style={{fontSize: 12}}>
            {languageOptions[languageIdx]}
          </b>
        </div>
      </div>

      <div className='flex justify-center items-center gap-3'>
        <div className='mt-8 flex-1'>
          <Title>Content filter</Title>
          <div style={{
            position: 'relative',
            height: 28
          }}
          onClick={() => {
            setProfile(v => ({
              ...(v ?? {}),
              commentFilter: commentFilterOptions[
                (commentFilterIdx+1)%commentFilterOptions.length
              ]
            }))
          }}>
            <div  style={{
              borderBottom: '2px solid black',
              position: 'absolute',
              top: 13,
              left: 0,
              width: '100%'
            }}/>
            <div  style={{
              borderBottom: '1px solid black',
              width: 14,
              height: 14,
              backgroundColor: '#D9D9D9',
              border: '3px solid #000',
              borderRadius: 7,
              position: 'absolute',
              top: 7,
              marginLeft: -7,
              transition: 'left 0.3s',
              left: `${2 + ((96/(commentFilterOptions.length-1)) * commentFilterIdx)}%`
            }}/>
          </div>
          <b style={{fontSize: 12}}>
            {commentFilterOptions[commentFilterIdx]}
          </b>
        </div>
      </div>

    </section>
  );
}
